package day1.examples;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int j = 2;
		
		switch(j) {
			
			case 0: 
				System.out.println("J = 0");
				break;
			case 1: 
				System.out.println("J = 1");
				break;
			case 2: 
				System.out.println("J = 2");
				break;
			default:
				System.out.println("no value");
				break;
		}
		
	}

}
